export const Button: React.FC<Props> = ({ 
    color,
    children,
    height,
    onClick, 
    width
  }) => { 
  return (
    <Wrapper>
      <Button 
        as='link'
        to='/Secret'
        styleType='primary'

        onClick={onClick}
        style={{
          backgroundColor: color,
          height,
          width
        }}
      >
      {children}
      </Button>
    </Wrapper>
  );
}

const Wrapper = styled.div`
  display: flex;
  flex-direction: row;
  margin-left: auto;
