import React from "react";
import styled from "styled-components";

interface Props {
  border: string;
  color: string;
  children?: React.ReactNode;
  height: string;
  onClick: () => void;
  radius: string
  width: string;
}

export const Button: React.FC<Props> = ({ 
    border,
    color,
    children,
    height,
    onClick, 
    radius,
    width
  }) => { 
  return (
    <Wrapper>
      <button 
        onClick={onClick}
        style={{
          backgroundColor: color,
          border,
          borderRadius: radius,
          height,
          width
        }}
      >
      {children}
      </button>
    </Wrapper>
  );
}

const Wrapper = styled.div`
  display: flex;
  flex-direction: row;
  @media only screen and (max-width: 600px) {
    flex-direction: column;
  }
  padding: 1px;
  border: 1px solid #3b403e;
  border-radius: 3%;
  margin-top: 20px;
  transition: transform 250ms;
  :hover {
    transform: translateY(-4px);
  }
`;
