export const Button: React.FC<Props> = ({ 
    color,
    children,
    height,
    onClick, 
    radius,
    width
  }) => { 
  return (
    <Wrapper>
      <button 
        onClick={onClick}
        style={{
          backgroundColor: color,
          borderRadius: radius,
          height,
          width
        }}
      >
      {children}
      </button>
    </Wrapper>
  );
}

